import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { useState, useEffect } from "react";
import { CartProvider } from "./components/CartContext";
import "./App.css";
import Header from "./components/Header";
import Products from "./components/Products";
import ProductDetail from "./components/ProductDetail";
import Profile from "./components/Profile";
import CartPage from "./components/CartPage";
import OrderConfirmation from "./components/OrderConfirmation";
import OrderHistory from "./components/OrderHistory";
import { useNavigate } from "react-router-dom";

function App() {
    const [user, setUser] = useState(null);
    const [toastMessage, setToastMessage] = useState("");

    // Load user from localStorage
    useEffect(() => {
        const storedUser = localStorage.getItem("userProfile");
        if (storedUser) {
            setUser(JSON.parse(storedUser));
        }
    }, []);
    function LoginRedirectNotice() {
        const navigate = useNavigate();
      
        useEffect(() => {
          // display toast
          setToastMessage("Redirecting to homepage...");
      
          const timer = setTimeout(() => {
            setToastMessage(""); // delete toast
            navigate("/");
          }, 2000);
      
          return () => clearTimeout(timer);
        }, [navigate]);
      
        return (
          <div className="login-reminder">
            <h2>Please log in to view your profile</h2>
          </div>
        );
      }
      

    return (
        <CartProvider> {/* ✅ Wrap the whole app in CartProvider */}
        <Router>
            <div className="app-container">
                {/* ✅ Pass user and setUser to Header */}
                <Header user={user} setUser={setUser} />
                <Routes>
                    <Route path="/" element={<Products />} />
                    <Route path="/product/:id" element={<ProductDetail />} />
                    <Route path="/profile" element={user ? <Profile user={user} setUser={setUser} /> : <LoginRedirectNotice />} />
                    <Route path="/cart" element={<CartPage />} />
                    <Route path="/order-confirmation" element={<OrderConfirmation />} />
                    <Route path="/order-history" element={<OrderHistory />} />
                </Routes>
                {toastMessage && <div className="toast-message">{toastMessage}</div>}
            </div>
        </Router>
        </CartProvider> 
    );
}

export default App;
