import React, { useState } from "react";
import { useCart } from "./CartContext";
import { useNavigate } from "react-router-dom";
import "./cartPage.css";

function CartPage() {
    const { cartItems, removeFromCart, updateQuantity, checkout } = useCart();
    const navigate = useNavigate();

    const [paymentMethod, setPaymentMethod] = useState("");
    const [tip, setTip] = useState(0);

    const totalPrice = cartItems.reduce((sum, item) => sum + Number(item.price) * item.quantity, 0);
    const finalTotal = totalPrice + Number(tip);

    const handleCheckout = () => {
        if (!paymentMethod) {
            alert("Please select a payment method before checkout.");
            return;
        }
        checkout(paymentMethod, tip);
        navigate("/order-confirmation"); // Redirect to confirmation page
    };

    return (
        <div className="cart-page">
            <h2>Your Shopping Cart</h2>
            {cartItems.length === 0 ? (
                <p>Your cart is empty.</p>
            ) : (
                <div className="cart-items">
                    {cartItems.map((item) => (
                        <div className="cart-card" key={item.id}>
                            <div className="cart-left">
                                <img src={require(`../images/${item.image}`)} alt={item.name} />
                                <div>
                                    <h3>{item.name}</h3>
                                    <p>${item.price}</p>
                                    <button onClick={() => removeFromCart(item.id)}>Remove</button>
                                </div>
                            </div>
                            <input
                                type="number"
                                value={item.quantity}
                                onChange={(e) => updateQuantity(item.id, parseInt(e.target.value))}
                            />
                        </div>
                    ))}
                </div>
            )}

            {/* Payment Method Selection */}
            <div className="payment-section">
                <h3>Select Payment Method:</h3>
                <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
                    <option value="">-- Select Payment Method --</option>
                    <option value="Cash">Cash</option>
                    <option value="Card">Card</option>
                    <option value="Mobile Money">Mobile Money</option>
                </select>
            </div>

            {/* Tip Section */}
            <div className="tip-section">
                <h3>Add a Tip:</h3>
                <input
                    type="number"
                    placeholder="Enter tip amount"
                    value={tip}
                    onChange={(e) => setTip(e.target.value)}
                />
            </div>

            {/* Checkout Button */}
            <h3>Total: ${finalTotal.toFixed(2)}</h3>
            <button onClick={handleCheckout}>Checkout</button>
        </div>
    );
}

export default CartPage;
