import { useEffect } from 'react';
import { gapi } from 'gapi-script';
import { googleLogout } from "@react-oauth/google";
import { useNavigate } from 'react-router-dom';
import StoreLogo from '../images/online-shop.png';
import LoginButton from "./login";
import "../styles.css"; // Import global styles
import { FaShoppingCart } from "react-icons/fa";
import { useState } from "react";


const clientId = "709514609698-9er2a02tiodh6gudshi2ahlvklhjq0ok.apps.googleusercontent.com";

function Header({ user, setUser, setView, cartItems, setViewCart, setCartItems }) {
    const navigate = useNavigate();

    const totalItems = cartItems.reduce((sum, item) => sum + item.quantity, 0);
    const [showCart, setShowCart] = useState(false);
    const [showSidebar, setShowSidebar] = useState(false);
    
    useEffect(() => {
        function start() {
            gapi.client.init({
                clientId: clientId,
                scope: ""
            });
        };
        gapi.load('client:auth2', start);
    }, []);

    const handleLogout = () => {
        googleLogout();
        setUser(null);
        setCartItems([]); //clear cart when logout
        localStorage.removeItem("userProfile"); // Clear profile data on logout
    };

    return (
        <header className="header-component">
            <img 
                src={StoreLogo} 
                alt="Store logo" 
                className="store-logo"
                onClick={() => navigate("/")} 
                style={{ cursor: "pointer" }} 
            />
            <h1 onClick={() => navigate("/")} style={{ cursor: "pointer" }}>Online Marketing</h1>
            
                <div className="login-button-container">
                    {/* ✅ Show login button if no user, otherwise show profile */}
                    <div className="cart-icon-wrapper">
                        <div
                            className="cart-icon"
                            onClick={() => setShowSidebar(!showSidebar)} 
                            style={{ cursor: "pointer" }}
                        >
                            <FaShoppingCart size={24} />
                            {cartItems?.length > 0 && (
                            <span className="cart-count">
                                {cartItems.reduce((sum, item) => sum + item.quantity, 0)}
                            </span>
                            )}
                        </div>
                        {showSidebar && (
                            <div className="cart-sidebar">
                                <div className="cart-sidebar-items">
                                {cartItems.map((item) => (
                                    <div key={item.id} className="sidebar-item">
                                    <img
                                        src={require(`../images/${item.image}`)}
                                        alt={item.name}
                                        className="sidebar-img"
                                    />
                                    <div className="sidebar-details">
                                        <p>{item.name}</p>
                                        <p>x{item.quantity}</p>
                                    </div>
                                    </div>
                                ))}
                                </div>

                                <button
                                className="go-cart-sidebar-btn"
                                onClick={() => {
                                    setShowSidebar(false);
                                    navigate("/cart");
                                }}
                                >
                                Go to cart
                                </button>
                            </div>
                            )}
                     </div>

                {user ? (
                    <div className="user-info">
                        <img src={user.picture || "/default-avatar.png"} alt="Profile" className="profile-pic-small" />
                        <span onClick={() => navigate("/profile")} style={{ cursor: "pointer" }}>
                            {user.name}
                        </span>
                        <button onClick={handleLogout} className="logout-btn">Logout</button>
                    </div>
                ) : (
                    <LoginButton setUser={setUser} />
                )}
            </div>
            
        </header>
    );
}

export default Header;
