import React, { useState } from "react";
import { useCart } from "./CartContext";
import { useNavigate } from "react-router-dom";
import "./cartPage.css";

function CartPage() {
    const { cartItems, removeFromCart, updateQuantity, checkout, orderNumber } = useCart();
    const navigate = useNavigate();

    const [tip, setTip] = useState(0);
    const [paymentMethod, setPaymentMethod] = useState("Credit Card");

    const totalPrice = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
    const finalTotal = totalPrice + parseFloat(tip || 0);

    const handleCheckout = () => {
        checkout();
        navigate("/order-confirmation");
    };

    return (
        <div className="cart-page">
            <h2>Your Shopping Cart</h2>
            {cartItems.length === 0 ? (
                <p>Your cart is empty.</p>
            ) : (
                <div className="cart-items">
                    {cartItems.map((item) => (
                        <div className="cart-card" key={item.id}>
                            <div className="cart-left">
                                <img src={require(`../images/${item.image}`)} alt={item.name} />
                                <div>
                                    <h3>{item.name}</h3>
                                    <p>${item.price.toFixed(2)}</p>
                                    <button onClick={() => removeFromCart(item.id)}>Remove</button>
                                </div>
                            </div>
                            <input
                                type="number"
                                value={item.quantity}
                                min="1"
                                onChange={(e) => updateQuantity(item.id, parseInt(e.target.value) || 1)}
                            />
                        </div>
                    ))}
                </div>
            )}

            {cartItems.length > 0 && (
                <div className="checkout-section">
                    <h3>Choose Payment Method</h3>
                    <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
                        <option value="Credit Card">Credit Card</option>
                        <option value="PayPal">PayPal</option>
                        <option value="Cash on Delivery">Cash on Delivery</option>
                    </select>

                    <h3>Tip Amount ($)</h3>
                    <input
                        type="number"
                        value={tip}
                        min="0"
                        onChange={(e) => setTip(e.target.value)}
                        placeholder="Enter tip amount"
                    />

                    <h3>Total: ${finalTotal.toFixed(2)}</h3>

                    <button onClick={handleCheckout} className="checkout-btn">
                        Proceed to Order
                    </button>
                </div>
            )}

            {orderNumber && <h3>Order Number: {orderNumber}</h3>}
        </div>
    );
}

export default CartPage;
