import React, { useState } from "react";
import { useCart } from "./CartContext";
import { useNavigate } from "react-router-dom";
import "./cartPage.css";

function CartPage() {
    const { cartItems, removeFromCart, updateQuantity, checkout } = useCart();
    const navigate = useNavigate();

    const [paymentMethod, setPaymentMethod] = useState("");
    const [tipPercentage, setTipPercentage] = useState(0);
    const [tip, setTip] = useState(0);

    const totalPrice = cartItems.reduce((sum, item) => sum + (parseFloat(item.price) * parseInt(item.quantity)), 0);

    const handleCheckout = () => {
        if (cartItems.length === 0) {
          alert("Your cart is empty. Please add items before checkout.");
          return;
        }
        
        if (!paymentMethod) {
          alert("Please select a payment method before checkout.");
          return;
        }
        
        if (isNaN(tipAmount)) {
          alert("Please enter a valid tip amount.");
          return;
        }
        
        checkout(paymentMethod, tipAmount);
        navigate("/order-confirmation");
      };

    const calculateTip = () => {
        if (tipPercentage > 0) {
            return totalPrice * (tipPercentage / 100);
        }
        return parseFloat(tip || 0);
    };
    
    const tipAmount = calculateTip();
    const finalTotal = totalPrice + tipAmount;

    return (
        <div className="cart-page">
            <h2>Your Shopping Cart</h2>
            {cartItems.length === 0 ? (
                <p>Your cart is empty.</p>
            ) : (
                <div className="cart-items">
                    {cartItems.map((item) => (
                        <div className="cart-card" key={item.id}>
                            <div className="cart-left">
                                <img src={require(`../images/${item.image}`)} alt={item.name} />
                                <div>
                                    <h3>{item.name}</h3>
                                    <p>${item.price}</p>
                                    <button onClick={() => removeFromCart(item.id)}>Remove</button>
                                </div>
                            </div>
                            <input
                                type="number"
                                min="1"
                                value={item.quantity}
                                onChange={(e) => {
                                    const newQuantity = Math.max(1, parseInt(e.target.value) || 1);
                                    updateQuantity(item.id, newQuantity);
                                }}
                            />
                        </div>
                    ))}
                </div>
            )}

            {/* Payment Method Selection */}
            <div className="payment-section">
                <h3>Select Payment Method:</h3>
                <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
                    <option value="">-- Select Payment Method --</option>
                    <option value="Cash">Cash</option>
                    <option value="Card">Card</option>
                    <option value="Mobile Money">Mobile Money</option>
                </select>
            </div>

            {/* Tip Section */}
            <div className="tip-section">
                <h3>Add Tip:</h3>
                <div className="tip-options">
                    <button 
                        className={tipPercentage === 10 ? "active" : ""}
                        onClick={() => {
                            setTipPercentage(10);
                            setTip(0);
                        }}
                    >
                        10%
                    </button>
                    <button 
                        className={tipPercentage === 15 ? "active" : ""}
                        onClick={() => {
                            setTipPercentage(15);
                            setTip(0);
                        }}
                    >
                        15%
                    </button>
                    <button 
                        className={tipPercentage === 20 ? "active" : ""}
                        onClick={() => {
                            setTipPercentage(20);
                            setTip(0);
                        }}
                    >
                        20%
                    </button>
                </div>
                <div className="custom-tip">
                    <span>or Custom: $</span>
                    <input
                        type="number"
                        min="0"
                        step="0.01"
                        placeholder="0.00"
                        value={tipPercentage > 0 ? "" : tip}
                        onChange={(e) => {
                            setTipPercentage(0);
                            setTip(Math.max(0, parseFloat(e.target.value) || 0));
                        }}
                    />
                </div>
                <div className="tip-amount">
                    Tip Amount: ${tipAmount.toFixed(2)}
                </div>
                <div className="total-display">
                    <h3>Order Summary</h3>
                    <div className="price-breakdown">
                        <span>Subtotal:</span>
                        <span>${totalPrice.toFixed(2)}</span>
                    </div>
                    <div className="price-breakdown">
                        <span>Tip:</span>
                        <span>${tipAmount.toFixed(2)}</span>
                    </div>
                    <div className="price-breakdown" style={{fontWeight: 'bold', fontSize: '1.1em'}}>
                        <span>Total:</span>
                        <span>${finalTotal.toFixed(2)}</span>
                    </div>
                    <button 
                        className="checkout-btn" 
                        onClick={handleCheckout}
                        disabled={cartItems.length === 0}
                    >
                        Checkout
                    </button>
                </div>
            </div>
        </div>
    );
}

export default CartPage;
