import React from "react";
import { useCart } from "./CartContext";
import { useNavigate } from "react-router-dom";
import "./orderConfirmation.css";

function OrderConfirmation() {
    const { cartItems, orderNumber, paymentMethod, tip } = useCart();
    const navigate = useNavigate();

    const totalPrice = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
    const finalTotal = totalPrice + parseFloat(tip || 0);

    return (
        <div className="order-confirmation">
            <h2>Order Confirmation</h2>
            {orderNumber ? (
                <>
                    <h3>Order Number: {orderNumber}</h3>
                    <div className="order-summary">
                        <h3>Items Purchased:</h3>
                        <ul>
                            {cartItems.map((item) => (
                                <li key={item.id}>
                                    <span>{item.name} (x{item.quantity})</span> - ${item.price * item.quantity}
                                </li>
                            ))}
                        </ul>

                        <h3>Payment Method: {paymentMethod}</h3>
                        <h3>Tip Amount: ${tip || 0}</h3>
                        <h3>Total Paid: ${finalTotal.toFixed(2)}</h3>
                    </div>

                    <button onClick={() => navigate("/")}>Back to Home</button>
                </>
            ) : (
                <p>No order found. Please complete a checkout first.</p>
            )}
        </div>
    );
}

export default OrderConfirmation;
