import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./products.css";

function Products() {
    // Complete product categories and data
    const categories = {
        "All Products": "all",
        "Electronics": ["Computer", "Phone", "TV", "Sound System", "Microwave"],
        "Furniture": ["Chair", "Desk", "Table", "Bookshelf"],
        "Clothing": ["Shirt", "Jacket", "Shoes", "Shorts", "Dress", "Socks"],
        "Home Appliances": ["Dishwasher", "Refrigerator", "Dryer", "Washing Machine"],
    };

    const products = [
        { id: 1, name: "Computer", image: "computer.png", price: "$800", category: "Electronics" },
        { id: 2, name: "Phone", image: "phone.png", price: "$600", category: "Electronics" },
        { id: 3, name: "TV", image: "tv.png", price: "$700", category: "Electronics" },
        { id: 4, name: "Sound System", image: "sound-system.png", price: "$400", category: "Electronics" },
        { id: 5, name: "Microwave", image: "microwave.png", price: "$120", category: "Electronics" },
        { id: 6, name: "Chair", image: "chair.png", price: "$40", category: "Furniture" },
        { id: 7, name: "Desk", image: "desk.png", price: "$100", category: "Furniture" },
        { id: 8, name: "Table", image: "table.png", price: "$120", category: "Furniture" },
        { id: 9, name: "Bookshelf", image: "bookshelf.png", price: "$50", category: "Furniture" },
        { id: 10, name: "Shirt", image: "shirt.png", price: "$20", category: "Clothing" },
        { id: 11, name: "Jacket", image: "jacket.png", price: "$60", category: "Clothing" },
        { id: 12, name: "Shoes", image: "shoes.png", price: "$70", category: "Clothing" },
        { id: 13, name: "Shorts", image: "shorts.png", price: "$25", category: "Clothing" },
        { id: 14, name: "Dress", image: "dress.png", price: "$30", category: "Clothing" },
        { id: 15, name: "Socks", image: "socks.png", price: "$5", category: "Clothing" },
        { id: 16, name: "Dishwasher", image: "dishwasher.png", price: "$300", category: "Home Appliances" },
        { id: 17, name: "Refrigerator", image: "refrigerator.png", price: "$900", category: "Home Appliances" },
        { id: 18, name: "Dryer", image: "dryer.png", price: "$250", category: "Home Appliances" },
        { id: 19, name: "Washing Machine", image: "washing-machine.png", price: "$850", category: "Home Appliances" }
    ];

    const [selectedCategory, setSelectedCategory] = useState("All Products");
    const navigate = useNavigate();

    const filteredProducts = selectedCategory === "All Products"
        ? products
        : products.filter(product => categories[selectedCategory].includes(product.name));

    return (
        <div className="products-page">
            <div className="sidebar">
                <h3>Categories</h3>
                <ul>
                    {Object.keys(categories).map((category) => (
                        <li 
                            key={category} 
                            onClick={() => setSelectedCategory(category)}
                            className={selectedCategory === category ? "active" : ""}
                        >
                            {category}
                        </li>
                    ))}
                </ul>
            </div>

            <div className="products-container">
                <h2>{selectedCategory}</h2>
                <div className="product-grid">
                    {filteredProducts.map((product) => (
                        <div key={product.id} className="product-item">
                            <img 
                                src={require(`../images/${product.image}`)} 
                                alt={product.name} 
                                className="product-image"
                                onClick={() => navigate(`/product/${product.id}`, { state: product })}
                                style={{ cursor: "pointer" }}
                            />
                            <h3>{product.name}</h3>
                            <p>{product.price}</p>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Products;
