import "./products.css"; // 引入全局样式

function Products() {
    // 分类的产品数据
    const categories = {
        "Electronics": [
            { id: 1, name: "Computer", image: "computer.png", price: "$800" },
            { id: 2, name: "Phone", image: "phone.png", price: "$600" },
            { id: 3, name: "TV", image: "tv.png", price: "$700" },
            { id: 4, name: "Sound System", image: "sound-system.png", price: "$400" },
            { id: 5, name: "Microwave", image: "microwave.png", price: "$120" },
        ],
        "Furniture": [
            { id: 6, name: "Chair", image: "chair.png", price: "$40" },
            { id: 7, name: "Desk", image: "desk.png", price: "$100" },
            { id: 8, name: "Table", image: "table.png", price: "$120" },
            { id: 9, name: "Bookshelf", image: "bookshelf.png", price: "$50" },
        ],
        "Clothing": [
            { id: 10, name: "Shirt", image: "shirt.png", price: "$20" },
            { id: 11, name: "Jacket", image: "jacket.png", price: "$60" },
            { id: 12, name: "Shoes", image: "shoes.png", price: "$70" },
            { id: 13, name: "Shorts", image: "shorts.png", price: "$25" },
            { id: 14, name: "Dress", image: "dress.png", price: "$30" },
            { id: 15, name: "Socks", image: "socks.png", price: "$5" },
        ],
        "Home Appliances": [
            { id: 16, name: "Dishwasher", image: "dishwasher.png", price: "$300" },
            { id: 17, name: "Refrigerator", image: "refrigerator.png", price: "$900" },
            { id: 18, name: "Dryer", image: "dryer.png", price: "$250" },
            { id: 19, name: "Washing Machine", image: "washing-machine.png", price: "$850" },
        ],
        "Stationery & Misc": [
            { id: 20, name: "Paper", image: "paper.png", price: "$3" },
            { id: 21, name: "Pasta", image: "pasta.png", price: "$4" },
            { id: 22, name: "Dairy", image: "dairy.png", price: "$6" },
            { id: 23, name: "Beans", image: "beans.png", price: "$5" },
            { id: 24, name: "Binding", image: "binding.png", price: "$7" },
            { id: 25, name: "Cleaning", image: "cleaning.png", price: "$10" },
            { id: 26, name: "Stationery", image: "stationery.png", price: "$15" },
        ],
    };

    return (
        <div className="products-container">
            <h2>Products</h2>
            {Object.entries(categories).map(([category, products]) => (
                <div key={category} className="category-section">
                    <h3 className="category-title">{category}</h3>
                    <div className="product-grid">
                        {products.map((product) => (
                            <div key={product.id} className="product-item">
                                <img 
                                    src={require(`../images/${product.image}`)} 
                                    alt={product.name} 
                                    className="product-image"
                                />
                                <h3>{product.name}</h3>
                                <p>{product.price}</p>
                            </div>
                        ))}
                    </div>
                </div>
            ))}
        </div>
    );
}

export default Products;
