import React, { useEffect } from "react";
import { useCart } from "./CartContext";
import { useNavigate } from "react-router-dom";
import "./orderConfirmation.css";

function OrderConfirmation() {
    const { 
        cartItems, 
        orderNumber, 
        paymentMethod, 
        tip,
        setCartItems // Add this
    } = useCart();
    const navigate = useNavigate();

    // Clear cart after displaying confirmation
    useEffect(() => {
        return () => {
            setCartItems([]);
        };
    }, [setCartItems]);

    const totalPrice = cartItems.reduce(
        (sum, item) => sum + (parseFloat(item.price) * parseInt(item.quantity)),
        0
    );
    const finalTotal = totalPrice + parseFloat(tip || 0);

    return (
        <div className="order-confirmation">
            <h2>Order Confirmation</h2>
            {orderNumber ? (
                <>
                    <h3>Order Number: {orderNumber}</h3>
                    <div className="order-summary">
                        <h3>Items Purchased:</h3>
                        {cartItems.length > 0 ? (
                            <ul>
                                {cartItems.map((item) => (
                                    <li key={item.id}>
                                        <span>{item.name} (x{item.quantity})</span> - $
                                        {(parseFloat(item.price) * parseInt(item.quantity)).toFixed(2)}
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p>No items in this order</p>
                        )}

                        <h3>Payment Method: {paymentMethod}</h3>
                        <h3>Tip Amount: ${parseFloat(tip || 0).toFixed(2)}</h3>
                        <h3>Total Paid: ${finalTotal.toFixed(2)}</h3>
                    </div>

                    <button onClick={() => navigate("/")}>Back to Home</button>
                </>
            ) : (
                <p>No order found. Please complete a checkout first.</p>
            )}
        </div>
    );
}

export default OrderConfirmation;
