import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./products.css";

function Products() {
    // Complete product data with detailed descriptions
    const products = [
        { id: 1, name: "Computer", image: "computer.png", price: "$800", description: "High-performance laptop with the latest CPU." },
        { id: 2, name: "Phone", image: "phone.png", price: "$600", description: "Smartphone with excellent camera quality." },
        { id: 3, name: "TV", image: "tv.png", price: "$700", description: "4K Ultra HD Smart TV with HDR support." },
        { id: 4, name: "Sound System", image: "sound-system.png", price: "$400", description: "Premium surround sound system." },
        { id: 5, name: "Microwave", image: "microwave.png", price: "$120", description: "Compact and powerful microwave oven." },
        { id: 6, name: "Chair", image: "chair.png", price: "$40", description: "Ergonomic office chair for maximum comfort." },
        { id: 7, name: "Desk", image: "desk.png", price: "$100", description: "Spacious wooden desk for home and office use." },
        { id: 8, name: "Table", image: "table.png", price: "$120", description: "Stylish dining table made of solid wood." },
        { id: 9, name: "Bookshelf", image: "bookshelf.png", price: "$50", description: "Modern bookshelf with multiple storage shelves." },
        { id: 10, name: "Shirt", image: "shirt.png", price: "$20", description: "Casual cotton shirt available in various colors." },
        { id: 11, name: "Jacket", image: "jacket.png", price: "$60", description: "Warm and stylish winter jacket." },
        { id: 12, name: "Shoes", image: "shoes.png", price: "$70", description: "Comfortable running shoes with great support." },
        { id: 13, name: "Shorts", image: "shorts.png", price: "$25", description: "Lightweight athletic shorts for everyday use." },
        { id: 14, name: "Dress", image: "dress.png", price: "$30", description: "Elegant evening dress for special occasions." },
        { id: 15, name: "Socks", image: "socks.png", price: "$5", description: "Soft and breathable socks for all-day comfort." },
        { id: 16, name: "Dishwasher", image: "dishwasher.png", price: "$300", description: "Efficient and quiet dishwasher with multiple cycles." },
        { id: 17, name: "Refrigerator", image: "refrigerator.png", price: "$900", description: "Spacious fridge with energy-saving technology." },
        { id: 18, name: "Dryer", image: "dryer.png", price: "$250", description: "High-speed clothes dryer with heat control settings." },
        { id: 19, name: "Washing Machine", image: "washing-machine.png", price: "$850", description: "Front-load washing machine with smart controls." }
    ];

    const [selectedCategory, setSelectedCategory] = useState("All Products");
    const navigate = useNavigate(); // Hook for navigation

    return (
        <div className="products-page">
            <div className="sidebar">
                <h3>Categories</h3>
                <ul>
                    <li className="active" onClick={() => setSelectedCategory("All Products")}>All Products</li>
                </ul>
            </div>

            <div className="products-container">
                <h2>{selectedCategory}</h2>
                <div className="product-grid">
                    {products.map((product) => (
                        <div key={product.id} className="product-item">
                            <img 
                                src={require(`../images/${product.image}`)} 
                                alt={product.name} 
                                className="product-image"
                                onClick={() => navigate(`/product/${product.id}`, { state: product })} // Pass product data
                                style={{ cursor: "pointer" }}
                            />
                            <h3>{product.name}</h3>
                            <p>{product.price}</p>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Products;
