import { useEffect } from 'react';
import { gapi } from 'gapi-script';
import { googleLogout } from "@react-oauth/google";
import { useNavigate } from 'react-router-dom';
import StoreLogo from '../images/online-shop.png';
import LoginButton from "./login";
import "../styles.css"; // Assuming global styles are still needed
import { FaShoppingCart } from "react-icons/fa";
import { useCart } from './CartContext';

const clientId = "709514609698-9er2a02tiodh6gudshi2ahlvklhjq0ok.apps.googleusercontent.com";

function Header({ user, setUser }) {
    const navigate = useNavigate();
    const { cartItems } = useCart();

    const totalItems = cartItems.reduce((sum, item) => sum + item.quantity, 0);

    useEffect(() => {
        function start() {
            gapi.client.init({
                clientId: clientId,
                scope: ""
            });
        };
        gapi.load('client:auth2', start);
    }, []);

    const handleLogout = () => {
        const confirmLogout = window.confirm("Are you sure you want to log out?");
        if (confirmLogout) {
            googleLogout();
            setUser(null);
            localStorage.removeItem("userProfile");
            navigate("/");
        }
    };

    return (
        <header className="header-component">
            <img
                src={StoreLogo}
                alt="Store logo"
                className="store-logo"
                onClick={() => navigate("/")}
            />
            <h1 onClick={() => navigate("/")} className="header-title">Online Marketing</h1>

            <div className="login-button-container">
                <div className="cart-icon-wrapper" onClick={() => navigate("/cart")}>
                    <FaShoppingCart className="cart-icon" size={24} />
                    {totalItems > 0 && (
                        <span className="cart-count">{totalItems}</span>
                    )}
                </div>
                {user ? (
                    <div className="user-details-component">
                        <img
                            src={user.picture || "/default-avatar.png"}
                            alt="Profile"
                            className="profile-pic-small"
                        />
                        <span
                            className="profile-link"
                            onClick={() => navigate("/profile")}
                        >
                            {user.name}
                        </span>
                        <button onClick={handleLogout} className="logout-btn">Logout</button>
                    </div>
                ) : (
                    <LoginButton setUser={setUser} />
                )}
            </div>
        </header>
    );
}

export default Header;
